import React, { Component } from 'react'
import ChartistGraph from 'react-chartist'
import { PieChart } from 'react-minimal-pie-chart';
import { Chart } from "react-google-charts";
const pieOptions = {
    title: "",
    pieHole: 0.6,
    slices: [
        {
            color: "#2BB673"
        },
        {
            color: "#d91e48"
        },
        {
            color: "#007fad"
        },
        {
            color: "#e9a227"
        }
    ],
    legend: {
        position: "bottom",
        alignment: "center",
        textStyle: {
            color: "233238",
            fontSize: 14
        }
    },
    tooltip: {
        showColorCode: true
    },
    chartArea: {
        left: 0,
        top: 0,
        width: "100%",
        height: "80%"
    },
    fontName: "Roboto"
};
class Dashboard extends Component {
    state = {
        chartImageURI: ""
    };
    render() {
        let dataPie = {
            labels: ["Madhya Pradesh", "Rajasthan", "Maharashtra"],
            series: [70, 20, 10]
        }
       
        return (
            <div className="content">
                <div className="container-fluid">
                    <div className="row text-center justify-content-center">

                        <div className="col-md-8">
                            <div className="card pieDiv">
                                <div className="card-header mb-5">
                                    <h4 className="card-title">Statewise Number of Colleges</h4>
                                    
                                </div>
                                <Chart
                                    chartType="PieChart"
                                    data={[["State", "Colleges"], ["Madhya Pradesh", 12], ["Rajasthan", 5], ["Himachal", 7]]}
                                    options={pieOptions}
                                    graph_id="PieChart"
                                    width={"100%"}
                                    height={"400px"}
                                    legend_toggle
                                    chartEvents={[
                                        {
                                            eventName: "select",
                                            callback: ({ chartWrapper, google }) => {
                                                var chart = chartWrapper.getChart();
                                                var selectedItem = chart.getSelection()[0];
                                                const dataTable = chartWrapper.getDataTable();
                                               //alert('The user selected ' + selectedItem);
                                               console.log(selectedItem);
                                                 // var topping = data.getValue(selectedItem.row, 0);
                                                // alert('The user selected ' + topping);
                                                // const chart = chartWrapper.getChart();
                                                // google.visualization.events.addListener(chart, "onmouseover", e => {
                                                //     const { row, column } = e;
                                                //     console.warn("MOUSE OVER ", { row, column });
                                                // });
                                                // google.visualization.events.addListener(chart, "onmouseout", e => {
                                                //     const { row, column } = e;
                                                //     console.warn("MOUSE OUT ", { row, column });
                                                // });
                                                alert('hello')
                                            }
                                        }
                                    ]}
                                />
                               
                            </div>
                        </div>
                        

                    </div>
                </div>
            </div>
        )
    }
}

export default Dashboard
